import React from 'react';
import { useState } from 'react';
import AddRoomModal from '../../components/AddRoomModal/AddRoomModal';
import RoomCard from '../../components/RoomCard/RoomCard';
import styles from './Rooms.module.css';
const rooms = [

  {
    id: 1,
    topic: 'Which framework is best for Web-Dev ?',
    speaker: [
      {
        id:1,
        name:'Jack Sparrow',
        avatar: '/images/monkey-avatar.png'
      },
      {
        id:2,
        name:'Heck Sparrow',
        avatar: '/images/monkey-avatar.png'
      },

    ],
    totalPeople: 40,
  },
  {
    id: 2,
    topic: 'Why ML is important ?',
    speaker: [
      {
        id:1,
        name:'Tony Stark',
        avatar: '/images/monkey-avatar.png'
      },
      {
        id:2,
        name:'Bruce Banner',
        avatar: '/images/monkey-avatar.png'
      },

    ],
    totalPeople: 60,
  },
  {
    id: 3,
    topic: 'Why AI is blooming ?',
    speaker: [
      {
        id:1,
        name:'Andrew',
        avatar: '/images/monkey-avatar.png'
      },
      {
        id:2,
        name:'John marston',
        avatar: '/images/monkey-avatar.png'
      },

    ],
    totalPeople: 30,
  },
  {
    id: 4,
    topic: 'Importance of Game Developement',
    speaker: [
      {
        id:1,
        name:'Trevor Philips',
        avatar: '/images/monkey-avatar.png'
      },
      {
        id:2,
        name:'Arthur Morgan',
        avatar: '/images/monkey-avatar.png'
      },

    ],
    totalPeople: 100,
  },

];

const Rooms = () => {

  const [showModal, setShowModal] = useState(false);
  function openModal(){
    setShowModal(true);
  }
  
  return <>
    <div className="container">
      <div className={styles.roomsHeader}>
        <div className={styles.left}>
          <span className={styles.heading}>All voice rooms</span>
          <div className={styles.searchBox}>
            <img src="/images/search-icon.png" alt="search" />
            <input type="text" className={styles.searchInput} />
          </div>
        </div>
        <div className={styles.right}>
          <button onClick={openModal} className={styles.startRoom}>
            <img src="/images/add-room-icon.png" alt="add-room" />
            <span>Start a room</span>
          </button>
        </div>
      </div>
      <div className={styles.roomList}>
          {rooms.map((room) => (
            <RoomCard key={room.id} room={room}/>
          ))
        }
      </div>
    </div>
    {showModal && <AddRoomModal />}
  </>;
};

export default Rooms;
